sys_script.script:
/*
 *Checks to see if the 'When needed' date is valid, and, if not, tells the user and aborts the save 
 */
(function executeRule(current, previous /*null when async*/) {
	var /*NeedItUtils*/ niUtils = new NeedItUtils();
	var /*bool*/ isPast = niUtils.isDatePast(current.u_when_needed);
	var /*bool*/ isToday = niUtils.isDateToday(current.u_when_needed);
	var /*bool*/ isWeekend = niUtils.isWeekend(current.u_when_needed);


	if(isPast){
		//tell user 'When needed' is in the past
		gs.addErrorMessage("'When needed' date cannot be in the past. Your request has not been saved to the database.");
		//abort the save
		current.setAbortAction(true);
	}
	else if(isToday){
		//tell user that same-day requests are not allowed
		gs.addErrorMessage("Same-day requests are not allowed. Your request has not been saved to the database.");
		//abort the save
		current.setAbortAction(true);
	}
	else if(isWeekend){
		//tell user 'When needed' is a weekend
		gs.addErrorMessage("'When needed' date cannot be a weekend. Your request has not been saved to the database.");
		//abort the save
		current.setAbortAction(true);
	}
})(current, previous);
